mapper.update:
UPDATE card_package_coupon
SET status = #{status}
WHERE card_package_id = #{cardPackageID}
    AND coupon_id = #{couponID}

mapper.select:
SELECT id
FROM card_package_coupon
WHERE card_package_id = #{cardPackageID}
  AND coupon_id = #{couponID}

SELECT *
FROM card_package_coupon
WHERE card_package_id = #{cardPackageID}
  AND coupon_id = #{couponID}
