mapper.update:
UPDATE coupon
SET collected_quantity = collected_quantity - 1
WHERE id = #{id}

UPDATE coupon
SET collected_quantity = collected_quantity + 1
WHERE id = #{id}

mapper.delete:
DELETE
FROM card_package_coupon
WHERE card_package_id = (
    SELECT card_package_id
    FROM user
    WHERE open_id = #{open_id}
)
  AND coupon_id = #{id}

DELETE
FROM coupon
WHERE merchant_id = #{merchantID}
  AND id = #{couponID}

mapper.update:
UPDATE coupon
SET status = 3
WHERE merchant_id = #{merchantID}
  AND id = #{couponID}

mapper.select:
SELECT img_url
FROM goods_detail_image
WHERE coupon_id = #{id}

SELECT merchant_id
FROM coupon
WHERE id = #{couponID}
